import { Component } from '@angular/core';

import pdfMake from "pdfmake/build/pdfmake";
import pdfFonts from "pdfmake/build/vfs_fonts";
pdfMake.vfs = pdfFonts.pdfMake.vfs;

class Product{
  name: string;
  price: number;
  qty: number;
}
class Invoice{
  customerName: string;
  address: string;
  contactNo: string;
  email: string;
  dni: string;

  
  products: Product[] = [];
  additionalDetails: string;

  constructor(){
    // Initially one empty product row we will show 
    this.products.push(new Product());
  }
}
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  invoice = new Invoice(); 
  
  generatePDF(action = 'open') {
    let docDefinition = {
      content: [
        {
          image: 'building',
          width: 150,
          alignment: 'center',
        },
        // {
        //   text: 'Race by car doctor',
        //   fontSize: 16,
        //   alignment: 'center',
        //   color: 'red',               
        //   image: 'data:src\assets\a.png',
        //   fit: [100, 100],
         
        // },
        {
          text: 'Recibo',
          fontSize: 20,
          bold: true,
          alignment: 'center',
          decoration: 'underline',
          color: 'Black'
        },
        {
          text: 'Detalles del cliente',
          style: 'sectionHeader'
        },
        {
          columns: [
            [
              {
                text: this.invoice.customerName,
                bold:true
              },
              { text: this.invoice.dni },
              { text: this.invoice.address },
              { text: this.invoice.email },
              { text: '0'+this.invoice.contactNo }
            ],
            [
              {
                text: `Fecha: ${new Date().toLocaleString()}`,
                alignment: 'right'
              },
              { 
                text: `Documento No. : ${((Math.random() *1000).toFixed(0))}`,
                alignment: 'right'
              }
            ]
          ]
        },
        {
          text: 'Detalles de la orden',
          style: 'sectionHeader'
        },
        {
          table: {
            headerRows: 1,
            widths: ['*', 'auto', 'auto', 'auto'],
            body: [
              ['Producto', 'Precio', 'Cantidad', 'Total'],
              ...this.invoice.products.map(p => ([p.name, p.price, p.qty, (p.price*p.qty).toFixed(2)])),
              [{text: 'Subtotal ',color: 'indigo',bold: true, colSpan: 3}, {}, {}, this.invoice.products.reduce((sum, p)=> sum + (p.qty * p.price), 0).toFixed(2)],
              [{text: 'IVA ',color: 'skyblue',bold: true, colSpan: 3}, {}, {}, this.invoice.products.reduce((sum, p)=>(sum + (p.qty * p.price)*0.12), 0).toFixed(2)],
              [{text: 'Total ',color: 'skyblue',bold: true, colSpan: 3}, {}, {}, this.invoice.products.reduce((sum, p)=> (sum + (p.qty * p.price))+(sum + (p.qty * p.price)*0.12), 0).toFixed(2)]
            ]
          }
        },
        {
          text: 'Detalles adicionales',
          style: 'sectionHeader'
        },
        {
            text: this.invoice.additionalDetails,
            margin: [0, 0 ,0, 15]          
        },
        {
          columns: [
            [{ qr: `${this.invoice.customerName}`, fit: '50' }],
            [{ text: 'Firma', alignment: 'right', italics: true}],
          ]
        },
        {
          text: 'Terminos y condiciones',
          style: 'sectionHeader'
        },
        {
            ul: [
              'Tiempo de validez: 15 días.',
              'La garantía del producto está sujeta a terminos y condiciones.',
              'Formas de pago: Efectivo/Tarjetas de crédito'
              ,'Descuentos aplican en pagos al contado',
              
            ],
        },  {
          ul: [
            'Con tarjeta de crédito a 3, 6 meses sin intereses',
            '9 y 12 meses con intereses'
            
          ],
      }
      ],
      images: {
        building: ' data:image/png;base64,'
          },
      styles: {
        sectionHeader: {
          bold: true,
          decoration: 'underline',
          fontSize: 14,
          margin: [0, 15,0, 15]          
        }
      }
    };

    if(action==='download'){
      pdfMake.createPdf(docDefinition).download();
    }else if(action === 'print'){
      pdfMake.createPdf(docDefinition).print();      
    }else{
      pdfMake.createPdf(docDefinition).open();      
    }

  }

  addProduct(){
    this.invoice.products.push(new Product());
  }
  
}
